import React, {useState} from "react";
import './App.css';

function App() {
  const [mailState, setMailState] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleThatChange = (e) => {
    setMailState((prevState) => ({
      ...prevState,
        [e.target.name]: e.target.value,
    }));
  }
  return (
    <div className="App">
     <form
        style={{
         display: "flex",
         height: "100vh",
         justifyContent: "center",
         alignItems: "center",
       }}
     >
       <fieldset
           style={{
           display: "flex",
           flexDirection: "column",
           justifyContent: "center",
           width: "50%",
         }}
       >
         <legend>React NodeMailer Contact Form</legend>
         <input
           placeholder="Name"
           onChange={handleThatChange}
           name="name"
           value={mailState.name}
         />
         <input
           placeholder="Email"
           onChange={handleThatChange}
           name="email"
           value={mailState.email}
         />
         <textarea
           placeholder="Message"
           onChange={handleThatChange}
           name="message"
           value={mailState.message}
         />
  <button>Send Email</button>
       </fieldset>
     </form>
    </div>
  );
}

export default App;
